a:visited{color:green }img[alt="Webhead.jpg"]{width:100%;height:auto}.editsection{display:none}h1:hover .editsection,h2:hover .editsection,h3:hover .editsection,h4:hover .editsection,h5:hover .editsection,h6:hover .editsection{display:inline}#nav h5,#nav div a{font-size:9.5pt }#nav div a{text-shadow:0.5ex 0.5ex 1ex silver}strike,s{opacity:.7 }pre,tt{border:1px solid #aaa;background:#f4f4f4}#spacestatus{position:relative;top:-32px;left:43px;border-radius:3px;width:80px;text-align:center;transform:rotate(-5deg);box-shadow:rgba(100,100,100,.6) 2px 2px 5px}#spacestatus.open{opacity:1;background:lime;color:black}#spacestatus.closed{opacity:1;background:red;color:white}#n{position:absolute;top:15px;left:5px;font-size:80%;background:gray;border-radius:3px;padding:0 1ex ;text-align:center;color:white;transform:rotate(-2deg);box-shadow:rgba(100,100,100,.6) 2px 2px 5px}#n a{display:inline;width:auto;height:auto;color:white}#n a:hover{color:lime}.spacestatus.spacestatus-kleur.open{color:green }.spacestatus.spacestatus-kleur.closed{color:red }#kiekjes{filter:saturate(130%) contrast(110%)}@keyframes spin{0%{transform:rotate(-0.8deg)}25%{transform:rotate(0.8deg)}50%{transform:rotate(-0.8deg)}75%{transform:rotate(0.8deg)}100%{transform:rotate(-0.8deg)}}#tilting{animation:spin 4s linear infinite}.tilt{transform:rotate(0.5deg)}